import React, {useState} from "react";
import {useNavigate}  from "react-router-dom";
const Signup =() =>{

    const navigate = useNavigate();

    const [user, setUser] = useState({
        name: "", email: "", password: ""
    });
    let name, value;
    const handleInput = (e) => {
        name = e.target.name;
        value = e.target.value;

        setUser({...user, [name]: value});
    } 
    const PostData = async(e) =>{
       e.preventDefault();
       const {name, eamil, password} = user;
       const res = await fetch("/registerUser", {
        method: "POST",
        headers: {
            "Content-Type" : "application/json"
        },
        body: JSON.stringify({
            name, email, password
        })
       }) ;
       const data = await res.json();
            if( data.status === 400 || !data){
                window.alert("Invalid registration")
            }
            else{
                window.alert("Registration Successfull");
                navigate("/login");
            }
    }
    return (
        <div className=" main1 text-center">
        <div className="form-signin w-100 m-auto">
            <form method="post">
                <img class="mb-4" src="images/logo2.svg" alt="my-logo" />
                <h1 class="h3 mb-3 fw-normal">Register</h1>
                <div class="form-floating">
                    <input type="name" onChange={handleInput} name="name" class="form-control" id="floatingInput" placeholder="Name" />
                    <label for="floatingInput">Enter Your Name</label>
                </div>
                <div class="form-floating">
                <input type="email" name="email" onChange={handleInput} class="form-control" id="floatingInput" placeholder="name@example.com" />
                <label for="floatingInput">Email address</label>
                </div>
                <div class="form-floating">
                <input type="password" name="password" onChange={handleInput} class="form-control" id="floatingPassword" placeholder="Password" />
                <label for="floatingPassword">Password</label>
                </div>
                <button class="w-100 btn btn-lg btn-primary" type="submit" onClick={PostData}>Register</button>
                <p class="mt-5 mb-3 text-muted">&copy; 2022 - Nadeem Ahmad </p>
            </form>
        </div>
        </div>
    )

}
export default Signup;
