import React from 'react'
import { useParams, useLocation } from 'react-router-dom';
const SingleProduct = (props) => {
  const {id} = useParams();
  const {description} = useLocation().state;
  return (
    <div className='main1'>
      <h1>{id}</h1>
      <h3>{description}</h3>
      <button>Add to Cart</button>
    </div>
  )
}

export default SingleProduct;
